import React, { useState, useEffect } from 'react';
const cursorStyles = {
    position: 'fixed',
    pointerEvents: 'none',
    zIndex: 100,
    width: '20px',
    height: '20px',
    backgroundColor: 'blue',
    borderRadius: '50%'
};

const trailStyles = {
    position: 'fixed',
    pointerEvents: 'none',
    zIndex: 99,
    width: '20px',
    height: '20px',
    backgroundColor: 'blue',
    borderRadius: '50%',
    animation: 'trail 0.3s linear'
};

const keyframes = `
@keyframes trail {
    from {
    opacity: 1;
    }
    to {
    opacity: 0;
    }
}
`;

const FollowCursor = () => {
    const [x, setX] = useState(0);
    const [y, setY] = useState(0);
    const [trails, setTrails] = useState([]);
    const onMouseMove = e => {
    setX(e.clientX);
    setY(e.clientY);
    setTrails([...trails, { x: e.clientX, y: e.clientY }]);
    };
    useEffect(() => {
    document.addEventListener("mousemove", onMouseMove);
    return () => document.removeEventListener("mousemove", onMouseMove);
    }, []);
    return (
    <>
        <style>{keyframes}</style>
        <div
            style={{
                ...cursorStyles,
                left: x,
                top: y
            }}
        />
        {trails.map((t, i) => (
        <div
            key={i}
            style={{
            ...trailStyles,
            left: t.x,
            top: t.y
            }}
        />
        ))}
    </>
    );
};

export default FollowCursor;
