import React, { useState, useEffect } from 'react';
import { CSSTransition, TransitionGroup } from 'react-transition-group';
const cursorStyles = {
    position: 'fixed',
    pointerEvents: 'none',
    zIndex: 100,
    width: '20px',
    height: '20px',
    backgroundColor: 'blue',
    borderRadius: '50%'
};

const trailStyles = {
    position: 'fixed',
    pointerEvents: 'none',
    zIndex: 99,
    width: '20px',
    height: '20px',
    backgroundColor: 'blue',
    borderRadius: '50%',
    animation: 'trail 0.3s linear'
};

const keyframes = `
@keyframes trail {
    from {
        opacity: 0.5;
    }
    to {
        opacity: 0;
    }
}`;

const FollowCursor = () => {
    const [trails, setTrails] = useState([]);
    const [trailsRef, setTrailsRef] = useState([]);

    const removeTrail = (index) => {
        setTrailsRef(trailsRef.filter((_, i) => i !== index));
    };

    const onMouseMove = e => {
        setTrails([...trails, { x: e.clientX, y: e.clientY }]);
    };

    useEffect(() => {
        setTrailsRef(trails);
    }, [trails]);

    useEffect(() => {
        document.addEventListener("mousemove", onMouseMove);
        return () => document.removeEventListener("mousemove", onMouseMove);
    }, []);

    return (
        <div>
            <style>{keyframes}</style>
            <TransitionGroup>
                {trailsRef.map((t, i) => (
                    <CSSTransition
                        key={i}
                        timeout={300}
                        classNames="trail"
                        onEntered={() => removeTrail(i)}
                    >
                        <div
                            style={{
                                ...trailStyles,
                                left: t.x,
                                top: t.y
                            }}
                        />
                    </CSSTransition>
                ))}
            </TransitionGroup>
        </div>
    );
};

export default FollowCursor;
