import React, { useState, useLayoutEffect } from 'react';
import { CSSTransition, TransitionGroup } from 'react-transition-group';
import { v4 as uuid } from 'uuid';

const trailStyles = {
    position: 'fixed',
    pointerEvents: 'none',
    zIndex: 99,
    width: '20px',
    height: '20px',
    backgroundColor: 'blue',
    borderRadius: '50%',
    animation: 'trail 200ms linear'
};

const FollowCursor = () => {
    const [trails, setTrails] = useState([]);

    useLayoutEffect(() => {
        console.log(trails);
        const onMouseMove = e => {
            setTrails([...trails, { x: e.clientX, y: e.clientY, startTime: new Date()}]);
        };
        document.addEventListener("mousemove", onMouseMove);
        return () => document.removeEventListener("mousemove", onMouseMove);
    }, [trails]);

    const removeTrail = (time) => {
        setTrails(trails.filter(t => t.startTime < time));
    };

    return (
        <div>
            <TransitionGroup>
                {trails.map((t, i) => (
                    <CSSTransition
                        key={i}
                        timeout={200}
                        classNames="trail"
                        onEntered={() => removeTrail(t.startTime)}
                    >
                        <div
                            style={{
                                ...trailStyles,
                                left: t.x,
                                top: t.y
                            }}
                        />
                    </CSSTransition>
                ))}
            </TransitionGroup>
        </div>
    );
};

export default FollowCursor;
