import React, { useState, useEffect } from 'react';
import { CSSTransition, TransitionGroup } from 'react-transition-group';

const FollowCursor = () => {
    const [trails, setTrails] = useState([]);
    const [trailsRef, setTrailsRef] = useState([]);

    const removeTrail = (index) => {
        setTrailsRef(trailsRef.filter((_, i) => i !== index));
    };

    const onMouseMove = e => {
        setTrails([...trails, { x: e.clientX, y: e.clientY }]);
    };

    useEffect(() => {
        setTrailsRef(trails);
    }, [trails]);

    useEffect(() => {
        document.addEventListener("mousemove", onMouseMove);
        return () => document.removeEventListener("mousemove", onMouseMove);
    }, []);

    return (
        <div>
            <style>{keyframes}</style>
            <div
                style={{
                    ...cursorStyles,
                    left: x,
                    top: y
                }}
            />
            <TransitionGroup>
                {trailsRef.map((t, i) => (
                    <CSSTransition
                        key={i}
                        timeout={300}
                        classNames="trail"
                        onEntered={() => removeTrail(i)}
                    >
                        <div
                            style={{
                                ...trailStyles,
                                left: t.x,
                                top: t.y
                            }}
                        />
                    </CSSTransition>
                ))}
            </TransitionGroup>
        </div>
    );
};


export default FollowCursor;
