import React, { useState, useLayoutEffect } from 'react';
import { CSSTransition, TransitionGroup } from 'react-transition-group';

const trailStyles = {
    position: 'fixed',
    pointerEvents: 'none',
    zIndex: 99,
    width: '40px',
    height: '40px',
    backgroundColor: 'blue',
    borderRadius: '50%',
};

const FollowCursor = () => {
    const [trails, setTrails] = useState([]);
    const [x, setX] = useState(0);
    const [y, setY] = useState(0);

    useLayoutEffect(() => {
        const onMouseMove = e => {
            const distance = Math.abs((y - e.clientY) + (x - e.clientX));
            if (distance > 5) {
                const startOpacity = Math.min(distance / 200, 0.8)
                setTrails([...trails, { x: e.clientX, y: e.clientY, startTime: new Date(), startOpacity: startOpacity }]);
            }
            setX(e.clientX);
            setY(e.clientY);
        };
        document.addEventListener("mousemove", onMouseMove);
        return () => document.removeEventListener("mousemove", onMouseMove);
    }, [trails, x, y]);

    const removeTrail = (t) => {
        setTrails(trails.filter(trailElement => trailElement !== t));
    };

    return (
        <div>
            <TransitionGroup>
                {trails.map((t, i) => (
                    <CSSTransition
                        key={i}
                        timeout={300}
                        classNames="trail"
                        onEntered={() => removeTrail(t)}
                    >
                        <div
                            style={{
                                ...trailStyles,
                                left: t.x,
                                top: t.y
                            }}
                        />
                    </CSSTransition>
                ))}
            </TransitionGroup>
        </div>
    );
};

export default FollowCursor;
