import React, { useRef, useEffect } from 'react';

const CanvasMouseEffect = () => {
    const canvasRef = useRef(null);

    useEffect(() => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
        const startOpacity = 0.4;
        const circles = [];
        let x = 0;
        let y = 0;
        let r = 0;

        // handle mouse movement
        const handleMouseMove = (event) => {
            const distY = Math.abs(x - event.clientX);
            const distX = Math.abs(y - event.clientY);    
            x = event.clientX;
            y = event.clientY;
            const distance = Math.sqrt(distX * distX + distY * distY);
            if (distance > 2) {
                r = (r + 1) % 255;
                let g = 0;
                let b = 0;
                circles.push({ x: x, y: y, opacity: startOpacity, r: r, g: g, b: b});
            }
        }

        window.addEventListener('mousemove', handleMouseMove);

        // animate the effect
        const animate = () => {
            // clear canvas
            ctx.clearRect(0, 0, canvas.width, canvas.height);

            // set canvas dimensions to window dimensions
            canvas.width = window.innerWidth;
            canvas.height = window.innerHeight;

            // draw circles
            for (let i = 0; i < circles.length; i++) {
                const circle = circles[i];
                if (circle.opacity <= 0) {
                    circles.splice(i--, 1);
                } else {
                    ctx.beginPath();
                    ctx.arc(circle.x, circle.y, 30 * Math.pow(((startOpacity * 2) - circle.opacity) / startOpacity, 2), 0, 2 * Math.PI);
                    ctx.fillStyle = `rgba(${circle.r}, ${circle.b}, ${circle.g}, ${circle.opacity})`;
                    ctx.fill();
                    circle.opacity -= (0.004 * (circles.length + 1));
                }
            }

            // repeat animation
            requestAnimationFrame(animate);
        }

        animate();

        return () => {
            window.removeEventListener('mousemove', handleMouseMove);
        }
    }, []);

    return (
        <canvas className={"follow-cursor-canvas"} ref={canvasRef} width={window.innerWidth} height={window.innerHeight} />
    );
};

export default CanvasMouseEffect;
