import React, { useState, useLayoutEffect } from 'react';
import { CSSTransition, TransitionGroup } from 'react-transition-group';

const trailStyles = {
    position: 'fixed',
    pointerEvents: 'none',
    zIndex: 99,
    backgroundColor: 'blue',
    borderRadius: '50%',
};

const FollowCursor = () => {
    const [trails, setTrails] = useState([]);
    const [x, setX] = useState(0);
    const [y, setY] = useState(0);

    useLayoutEffect(() => {
        const onMouseMove = e => {
            const distY = Math.abs(x - e.clientX);
            const distX = Math.abs(y - e.clientY);    
            const size = Math.sqrt(distX * distX + distY * distY);
            setTrails([...trails, { x: e.clientX, y: e.clientY, startTime: new Date(), size: size }]);
            setX(e.clientX);
            setY(e.clientY);
        };
        document.addEventListener("mousemove", onMouseMove);
        return () => document.removeEventListener("mousemove", onMouseMove);
    }, [trails, x, y]);

    const removeTrail = (t) => {
        setTrails(trails.filter(trailElement => trailElement.time > t.time));
    };

    return (
        <div>
            <TransitionGroup>
                {trails.map((t, i) => (
                    <CSSTransition
                        key={i}
                        timeout={300}
                        classNames="trail"
                        onEntered={() => removeTrail(t)}
                    >
                        <div
                            style={{
                                ...trailStyles,
                                left: t.x,
                                top: t.y,
                                width: t.size,
                                height: t.size
                            }}
                        />
                    </CSSTransition>
                ))}
            </TransitionGroup>
        </div>
    );
};

export default FollowCursor;
