import React, { useRef, useEffect } from 'react';

const CanvasMouseEffect = () => {
    const canvasRef = useRef(null);

    useEffect(() => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
        const circles = [];

        // handle mouse movement
        const handleMouseMove = (event) => {
            let x = event.clientX;
            let y = event.clientY;
            circles.push({ x: x, y: y, opacity: 0.5, color: '#000000'});
        }

        window.addEventListener('mousemove', handleMouseMove);

        // animate the effect
        const animate = () => {
            // clear canvas
            ctx.clearRect(0, 0, canvas.width, canvas.height);

            // set canvas dimensions to window dimensions
            canvas.width = window.innerWidth;
            canvas.height = window.innerHeight;

            // change color and decrease opacity
            color = `rgb(${Math.random() * 255}, ${Math.random() * 255}, ${Math.random() * 255})`;

            // draw circles
            for (let i = 0; i < circles.length; i++) {
                const circle = circles[i];
                if (circle.opacity <= 0) {
                    circles.splice(i--, 1);
                } else {
                    ctx.beginPath();
                    ctx.arc(circle.x, circle.y, 50, 0, 2 * Math.PI);
                    ctx.fillStyle = `rgba(${circle.color}, ${circle.opacity})`;
                    ctx.fill();
                    circle.opacity -= (0.1 * circles.length);
                }
            }

            // repeat animation
            requestAnimationFrame(animate);
        }

        animate();

        return () => {
            window.removeEventListener('mousemove', handleMouseMove);
        }
    }, []);

    return (
        <canvas className={"follow-cursor-canvas"} ref={canvasRef} width={window.innerWidth} height={window.innerHeight} />
    );
};

export default CanvasMouseEffect;
