import React, { useRef, useState, useEffect } from 'react';
import myWorker from '../../public/js/myWorker.js';

function AudioVisualizer(props) {
    const [audioElement, setAudioElement] = useState(null);
    const [loaded, setLoaded] = useState(false);
    const canvasAreaRef = useRef(null);
    const fileInputRef = useRef(null);
    const frequencyRef = useRef(null);
    const backgroundRef = useRef(null);
    const scrubBarRef = useRef(null);
    const [play, setPlay] = useState(false);
    const [mouseOverBottom, setMouseOverBottom] = useState(false);

    // create a new web worker
    const worker = new Worker(myWorker);

    useEffect(() => {
        // send the file input and canvas refs to the worker
        worker.postMessage({ fileInputRef, canvasAreaRef });

        worker.onmessage = (e) => {
            // handle messages from the worker
            if (e.data.type === 'loaded') {
                setLoaded(true);
            } else if (e.data.type === 'scrubBarValue') {
                scrubBarRef.current.value = e.data.value;
            }
        };

        return () => {
            // stop the worker when the component unmounts
            worker.terminate();
        };
    }, []);

    // Initialize audio context and elements when the file input changes
    function loadAudio() {
        if (audioElement) {
            audioElement.pause();
            audioElement.currentTime = 0;
        }
        setAudioElement(new Audio());
    }

    function updatePlayback() {
        if(audioElement && scrubBarRef.current) {
            let scrubBarValue = scrubBarRef.current.value;
            let currentTime = (scrubBarValue / 100) * audioElement.duration;
            audioElement.currentTime = currentTime;
        }
    }

    function togglePlay() {
        if (audioElement) {
            if (audioElement.paused) {
                audioElement.play();
                setPlay(true);
            } else {
                audioElement.pause();
                setPlay(false);
            }
        }
    }

    if (!audioElement) {
        loadAudio();
    }

    return (
        <div style={props.style} className='canvas-area no-select' ref={canvasAreaRef}>
            <br />
            <br />
            <div className="main-header-container">
                <h2 className={"main-header"} style={play?{opacity: 0, transition: "2s opacity"}:{opacity: 0.8, transition: "3s opacity"}}>David Mash</h2>
            </div>
            {loaded?
            <div className="control-panel" onClick={togglePlay}>
                {play?
                <img alt={""} src={process.env.PUBLIC_URL + '/images/pause-button.png'} className={"pause-button"} />
                :
                <img alt={""} src={process.env.PUBLIC_URL + '/images/play-button.png'} className={"play-button"} />
                }
            </div>
            :
            <></>
            }
            <br />
            <canvas ref={frequencyRef} id="frequency" onClick={togglePlay}/>
            <canvas ref={backgroundRef} id="background" />
            <img alt={""} src={process.env.PUBLIC_URL + '/images/main_images/' + props.imageNumber + '.jpeg'} style={{objectFit: "cover", height: "100%", minWidth: "100%", width: "auto", position: "absolute", zIndex: "-3", top: "0px", left: "0px"}}/>
            <div
                className='bottom-controls'
                onMouseOver={() => {if (!mouseOverBottom) setMouseOverBottom(true);}}
                onMouseLeave={() => {if (mouseOverBottom) setMouseOverBottom(false);}}
                style={mouseOverBottom?{opacity: 0.8, transition: "opacity 300ms"}:play?{opacity: 0, transition: "opacity 300ms"}:{opacity: 0.4, transition: "opacity 300ms"}}
            >
                <input
                    ref={scrubBarRef}
                    className="scrub-bar"
                    type="range"
                    min="0"
                    max="100"
                    defaultValue={0}
                    id="scrub-bar"
                    disabled={!loaded}
                    onChange={() => {updatePlayback();}}
                />
                {/*<input className={"file-input"} type="file" ref={fileInputRef} onChange={loadAudio} />*/}
            </div>
        </div>
    );
}

export default AudioVisualizer;
